use crate::manifest;

pub struct Assets {
    pub main_css: CssAsset,
    pub hasui_light_jpeg: ImageAsset,
    pub hasui_dark_jpeg: ImageAsset,
    pub browser_js: JsAsset,
    pub browser_bg_wasm: WasmAsset,
}

pub static assets: Assets = Assets {
    main_css: CssAsset {
        url: "main.css",
        relative_path: "target/tailwind/built.css",
    },
    hasui_light_jpeg: ImageAsset {
        url: "hasui-light.jpeg",
        bytes: include_bytes!("../routes/hasui_light_jpeg/hasui_light.jpeg"),
        lqip: "data:image/jpeg;base64,",
    },
    hasui_dark_jpeg: ImageAsset {
        url: "hasui-dark.jpeg",
        bytes: include_bytes!("../routes/hasui_dark_jpeg/hasui_dark.jpeg"),
        lqip: "data:image/jpeg;base64,",
    },
    browser_js: JsAsset {
        url: "browser.js",
        contents: include_str!("../../../target/browser/browser.js"),
    },
    browser_bg_wasm: WasmAsset {
        url: "browser_bg.wasm",
        bytes: include_bytes!("../../../target/browser/browser_bg.wasm"),
    },
};

pub struct CssAsset {
    pub url: &'static str,
    pub relative_path: &'static str,
}

impl CssAsset {
    pub fn absolute_path(&self) -> String {
        manifest::dir()
            .join(self.relative_path)
            .to_str()
            .unwrap()
            .to_string()
    }
}

pub struct ImageAsset {
    pub url: &'static str,
    pub bytes: &'static [u8],
    pub lqip: &'static str,
    pub alt: &'static str,
}

impl ImageAsset {
    pub fn srcset(&self) -> String {
        Self::image_widths()
            .iter()
            .map(|width| format!("{} {}w", self.url_with_width(*width), width))
            .collect::<Vec<String>>()
            .join(", ")
    }

    fn image_widths() -> Vec<u32> {
        vec![
            16, 32, 48, 64, 96, 128, 256, 384, 512, 640, 768, 896, 1024, 1152, 1280, 1408, 1536,
            1664, 1792, 1920, 2048, 2176, 2304, 2432, 2560, 2688, 2816, 2944, 3072, 3200, 3328,
            3456, 3584,
        ]
    }

    fn url_with_width(&self, width: u32) -> String {
        format!("{}?width={}", self.url, width)
    }
}

pub struct JsAsset {
    pub url: &'static str,
    pub contents: &'static str,
}

pub struct WasmAsset {
    pub url: &'static str,
    pub bytes: &'static [u8],
}
