use std::fs;

pub fn built_css() -> String {
    let path = built_css_path();
    fs::read_to_string(path).unwrap()
}

pub fn built_css_path() -> &'static str {
    "target/tailwind/built.css"
}

pub fn hasui_mountains_jpeg() -> Vec<u8> {
    fs::read(hasui_mountains_jpeg_path()).unwrap()
}

pub fn hasui_mountains_jpeg_path() -> &'static str {
    "src/routes/hasui_mountains_jpeg/hasui_mountains.jpeg"
}

pub fn hasui_mountains_lqip_url() -> &'static str {
    "data:image/jpeg;base64,"
}
